INSERT INTO `content` VALUES (1,'<h2>Da ! Heard It Records</h2>\r\n<ul>\r\n  <li><a href=\"http://www.discogs.com/label/Da%20!%20Heard%20It%20Records\">Discogs</a></li>\r\n  <li><a href=\"https://www.facebook.com/dahearditrecords\">Facebook</a></li>\r\n  <li><a href=\"http://freemusicarchive.org/label/Da__Heard_It_Records/\">Free Music Archive</a></li>\r\n  <li><a href=\"https://www.instagram.com/dahearditrecords/\">Instagram</a></li>\r\n  <li><a href=\"https://twitter.com/#!/daheardit\">Twitter</a></li>\r\n  <li><a href=\"https://vimeo.com/channels/dahearditrecords\">Vimeo</a></li>\r\n</ul>\r\n\r\n','<h2>Distribution</h2>\n<ul>\n  <li><a href=\"https://ruedesgardes.incongru.org\">Rue des Gardes</a></li>\n <li><a href=\"https://www.baladessonores.com/\">Balades Sonores</a></li>\n</ul>\n','<iframe width=\"560\" height=\"315\" src=\"https://www.youtube.com/embed/LUArnIZnJ3k?list=PLgpmTifGJBPQGp_JLT572YE7wV1gWTEpN\" frameborder=\"0\" allowfullscreen></iframe>','2012-07-05 12:40:19','2019-10-01 20:37:41');
